Scripts.OnLoad:
this:SetScale(0.9) local cb_init_fn = function(self, level) if level == 1 then if FGC_Patch_GetMax() > 1 then for i=1, FGC_Patch_GetMax() do local info = UIDropDownMenu_CreateInfo() info.text = FGC_Patch_GetText(i) info.hasArrow = true info.value = FGC_Patch_GetValue(i) UIDropDownMenu_AddButton(info, level) end else for i=1, FGC_Inst_GetMax(FGC_Patch_GetValue(1)) do local info = UIDropDownMenu_CreateInfo() info.checked = false info.text = FGC_Inst_Text(i, FGC_Patch_GetValue(1)) info.func = function() FGC_Inst_Click(i, FGC_Patch_GetValue(1)); CloseDropDownMenus(); end if i == FGC_Inst_Geti2(FGC_Patch_GetValue(1)) then info.checked = true end UIDropDownMenu_AddButton(info, level) end end local info = UIDropDownMenu_CreateInfo() info.checked = false info.text = FGC_Rand_GetText() info.func = function() FGC_Rand_Click(); CloseDropDownMenus(); end if FGC_Rand_GetStat() then info.checked = true end UIDropDownMenu_AddButton(info, level)
elseif (level == 2) then for i=1, FGC_Inst_GetMax(UIDROPDOWNMENU_MENU_VALUE) do local info = UIDropDownMenu_CreateInfo() info.checked = false info.text = FGC_Inst_Text(i, UIDROPDOWNMENU_MENU_VALUE) info.func = function() FGC_Inst_Click(i, UIDROPDOWNMENU_MENU_VALUE); CloseDropDownMenus(); end if i == FGC_Inst_Geti2(UIDROPDOWNMENU_MENU_VALUE) then info.checked = true end UIDropDownMenu_AddButton(info, level) end end
end UIDropDownMenu_Initialize(this, cb_init_fn)

Scripts.OnClick:
if FindGroupShowTextEditText:HasFocus() or FindGroupFrameSec:HasFocus() or FindGroupFrameEditTank:HasFocus() or FindGroupFrameEditHeal:HasFocus() or FindGroupFrameEditDD:HasFocus() then
FGC_TriggerButtonOff()
else
PlaySound("igMainMenuOptionCheckBoxOn");
FindGroup_CreateTrigger()
end
FindGroupShowTextEditText:ClearFocus();
FindGroupFrameSec:ClearFocus();
FindGroupFrameEditTank:ClearFocus();
FindGroupFrameEditHeal:ClearFocus();
FindGroupFrameEditDD:ClearFocus();

Scripts.OnValueChanged:
local minValue, maxValue = this:GetMinMaxValues();
FindGroupFrameEditTank:SetNumber(maxValue - this:GetValue());
FindGroupFrameEditHeal:SetNumber(minValue + this:GetValue());
if FGL.db.includeaddon then
FindGroupFrameSliderHealDD:SetMinMaxValues(0, FindGroupFrameEditDD:GetNumber()+FindGroupFrameEditHeal:GetNumber())
FindGroupFrameSliderHealDD:SetValue(FindGroupFrameEditDD:GetNumber())
end
--FindGroupFrameSliderTankHeal:SetValue(0)

local minValue, maxValue = this:GetMinMaxValues();
FindGroupFrameEditHeal:SetNumber(maxValue - this:GetValue());
FindGroupFrameEditDD:SetNumber(minValue + this:GetValue());
FindGroupFrameSliderTankHeal:SetMinMaxValues(0, FindGroupFrameEditHeal:GetNumber()+FindGroupFrameEditTank:GetNumber())
FindGroupFrameSliderTankHeal:SetValue(FindGroupFrameEditHeal:GetNumber())
--FindGroupFrameSliderTankHeal:SetValue(0)

Scripts.OnClick:
FindGroupShowTextEditText:ClearFocus();
FindGroupFrameSec:ClearFocus();
FindGroupFrameEditTank:ClearFocus();
FindGroupFrameEditHeal:ClearFocus();
FindGroupFrameEditDD:ClearFocus();
PlaySound("igMainMenuOptionCheckBoxOn");
						FGC_calc()

Scripts.OnShow:
local scrollbar = _G[self:GetName().."ScrollBar"];
scrollbar:SetValue(0);
local min;
local max;
min, max = scrollbar:GetMinMaxValues();
_G[scrollbar:GetName().."ScrollUpButton"]:Disable();
if ((scrollbar:GetValue() - max) == 0) then
	_G[scrollbar:GetName().."ScrollDownButton"]:Disable();
else
	_G[scrollbar:GetName().."ScrollDownButton"]:Enable();
end

local scrollbar = _G[self:GetName().."ScrollBar"];
scrollbar:SetValue(0);
local min;
local max;
min, max = scrollbar:GetMinMaxValues();
_G[scrollbar:GetName().."ScrollUpButton"]:Disable();
if ((scrollbar:GetValue() - max) == 0) then
	_G[scrollbar:GetName().."ScrollDownButton"]:Disable();
else
	_G[scrollbar:GetName().."ScrollDownButton"]:Enable();
end
